// HomeServ.js
import React from 'react';
import { View, Text, ImageBackground, Image, StyleSheet } from 'react-native';

const HomeServ = () => {
  // Replace 'path-to-your-background-image.jpg' with the actual path to your background image file
  const backgroundImage = require('/assets');
  const logo = require('./path-to-your-logo.png');

  // Array of names of the people who invented the app
  const inventors = [
    'Inventor 1',
    'Inventor 2',
    'Inventor 3',
    'Inventor 4',
    'Inventor 5',
    'Inventor 6',
  ];

  return (
    <ImageBackground source={backgroundImage} style={styles.background}>
      <View style={styles.container}>
        <Image source={logo} style={styles.logo} />
        <Text style={styles.text}>About Page</Text>

        {/* Display the names of the inventors */}
        <View style={styles.inventorsContainer}>
          {inventors.map((inventor, index) => (
            <Text key={index} style={styles.inventorText}>
              {inventor}
            </Text>
          ))}
        </View>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  background: {
    flex: 1,
    resizeMode: 'cover', // or 'stretch'
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  logo: {
    width: 100, // Adjust the width as needed
    height: 100, // Adjust the height as needed
    marginBottom: 20,
  },
  text: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'white', // Change the text color to make it visible on the background
  },
  inventorsContainer: {
    marginTop: 10,
  },
  inventorText: {
    fontSize: 16,
    marginBottom: 5,
    color: 'white', // Change the text color to make it visible on the background
  },
});

export default HomeServ;
