// HomeServ.js
import React from 'react';
import { View, Text, ImageBackground, Image, StyleSheet, Dimensions } from 'react-native';

const HomeServ = () => {
  const backgroundImage = require('/assets/abhiss.png');
  const logo = require('/assets/logo.png');
  const { width, height } = Dimensions.get('window');

  const inventors = [
    'ABHISHEK P',
    'ABHISHEK CS',
    'RAM SUNDAR HARIKUMAR',
    'AGNEL',
    'ARJUN',
    'ATHUL',
  ];

  return (
    <ImageBackground source={backgroundImage} style={[styles.background, { width, height }]}>
      <View style={styles.container}>
        <Image source={logo} style={[styles.logo, { opacity: 0.7 }]} />
        <Text style={[styles.title, { opacity: 0.8 }]}>About Us</Text>

        <View style={styles.inventorsContainer}>
          {inventors.map((inventor, index) => (
            <Text key={index} style={[styles.inventorText, { opacity: 0.6 }]}>
              {inventor}
            </Text>
          ))}
        </View>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  background: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  logo: {
    width: 150,
    height: 150,
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 20,
  },
  inventorsContainer: {
    marginTop: 10,
    alignItems: 'center',
  },
  inventorText: {
    fontSize: 18,
    color: 'white',
    marginBottom: 10,
  },
});

export default HomeServ;
