// HomeServ.js
import React from 'react';
import { View, Text, ImageBackground, Image, StyleSheet } from 'react-native';

const HomeServ = () => {
  // Replace 'path-to-your-background-image.jpg' with the actual path to your background image file
  const backgroundImage = require('/assets/abhiss.png');
  const logo = require('/assets/logo.png');

  // Array of names of the people who invented the app
  const inventors = [
    'ABHISHEK P',
    'Inventor 2',
    'Inventor 3',
    'Inventor 4',
    'Inventor 5',
    'Inventor 6',
  ];

  return (
    <ImageBackground source={backgroundImage} style={styles.background}>
      <View style={styles.container}>
        {/* Set opacity for the entire container */}
        <Image source={logo} style={[styles.logo, { opacity: 0.7 }]} />
        <Text style={[styles.title, { opacity: 0.8 }]}>About Us</Text>

        {/* Display the names of the inventors with different opacities */}
        <View style={styles.inventorsContainer}>
          {inventors.map((inventor, index) => (
            <Text key={index} style={[styles.inventorText, { opacity: 0.6 }]}>
              {inventor}
            </Text>
          ))}
        </View>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  background: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  logo: {
    width: 150,
    height: 150,
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 20,
  },
  inventorsContainer: {
    marginTop: 10,
    alignItems: 'center',
  },
  inventorText: {
    fontSize: 18,
    color: 'white',
    marginBottom: 10,
  },
});

export default HomeServ;
